import logo from './logo.svg';
import './App.css';
import { Formik, Field, Form } from "formik";

function App() {

  products = [
    "Produto 1",
    "Produto 2",
    "Produto 3",
    "Produto 4",
  ];

  const possibleProducts = products.map((product, key) => {
    <option value={product} key={"productOption-"+key}>
      {product}
    </option>
  })

  return (
    <div className="App">
      <h1>Contact Us</h1>
      <Formik
        initialValues={{ name: "", email: "" }}
        onSubmit={async (values) => {
          await new Promise((resolve) => setTimeout(resolve, 500));
          alert(JSON.stringify(values, null, 2));
        }}
      >
        <Form>
          <Field name="name" type="text" placeholder="Nome Completo" />
          <Field name="email" type="email" placeholder="E-mail"/>
          <Field name="product" type="select">
            <option value="">Selecione um produto</option>
            {possibleProducts}
          </Field>
          <label htmlFor=''use-terms>
            <Field name="use-terms"type="checkbox" />
            Aceito os <a href="#">termos de uso</a>.

          </label>
          <button type="submit">Enviar</button>
        </Form>
      </Formik>
    </div>
  );
}

export default App;
